package com.gmail.nmessaoudene.newsapp
import adapters.CategoryAdapter
import adapters.OnItemClickListener
import android.os.Bundle
import android.util.Log
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ArrayAdapter
import android.widget.Spinner
import android.widget.Toast
import androidx.fragment.app.Fragment
import androidx.fragment.app.FragmentActivity
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import model.Category

class CategoryList: Fragment(), OnItemClickListener {
    private lateinit var recyclerView: RecyclerView
    private lateinit var categoryAdapter: ArrayAdapter<String>

    override fun onCreateView(
        inflater: LayoutInflater,
        container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.category_list, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        recyclerView = view.findViewById(R.id.recyclerView)
        bindRecyclerView()
    }

    override fun onItemClicked(category: Category) {
        activity?.change(ListArticleFragment.newInstance(category.name))
    }

    private fun bindRecyclerView() {
        //créer une liste d'articles
        val categories = listOf<Category>(
            Category(name="Politique", description="Waouuuu c'est super la politique", url="https://images.sudouest.fr/2019/01/31/5c53448666a4bd53506a492b/widescreen/1000x500/emmanuel-macron-dans.jpg?v1"),
            Category(name="Economie", description="Blabla bla la éconmie crise économique ........ aie !", url="https://img.huffingtonpost.com/asset/5d1c694a2400005f00faa4cc.jpeg?cache=GUgxNHoen3&ops=1200_630"),
            Category(name="Education", description="Super retour à l'école !! ", url="https://resize-europe1.lanmedia.fr/r/622,311,forcex,center-middle/img/var/europe1/storage/images/europe1/societe/coronavirus-quatre-questions-sur-un-retour-a-lecole-qui-sannonce-tres-complique-3963692/54750700-1-fre-FR/Coronavirus-quatre-questions-sur-un-retour-a-l-ecole-qui-s-annonce-tres-complique.jpg"),
            Category(name="Pandémie", description="On va tous mourir !!!!! ", url="https://m1.quebecormedia.com/emp/emp/asdasdasdasddasdasdf8283d01-5947-42a8-977c-6b0cf2a66a4c_ORIGINAL.jpg?impolicy=crop-resize&x=0&y=0&w=0&h=0&width=650&height=650"),
            Category(name="Sciences", description="Scientifique ", url="https://cdn.futura-sciences.com/buildsv6/images/wide1920/a/0/2/a0269d7a2e_50155960_science-20e-siecle-artinspiring-fotolia.jpg"),
            Category(name="Ecologie", description="vive la planete ", url="data:image/png;base64,")
        )
        //créer une instance de l'adapteur
        val adapterRecycler = CategoryAdapter(categories, this)
        //définir l'orientation des élements (vertical)
        recyclerView.layoutManager =
            LinearLayoutManager(context)
        //associer l'adapter à la recyclerview
        recyclerView.adapter = adapterRecycler
    }
}